      uint8_t *dst_pixel = dst_row;
      for (x = 0; x < w; ++x) {
         uint32_t pixel = *src_pixel++;
         uint8_t a = (pixel & 0xff);
         uint8_t r = ((pixel >> 8) & 0xff);
         uint8_t g = ((pixel >> 16) & 0xff);
         uint8_t b = (pixel >> 24);
         *dst_pixel++ = r; /* r */
         *dst_pixel++ = g; /* g */
         *dst_pixel++ = b; /* b */
         *dst_pixel++ = a; /* a */
      }
